import React from "react";

const PostPage = ({ title, body, comments }) => {
  return (
    <>
      <h1>{title}</h1>
      <p>{body}</p>

      {comments && comments.length !== 0 ? (
        <ul>
          {comments.map(comment => (
            <li key={comment.id}>{comment.body}</li>
          ))}
        </ul>
      ) : (
        <h3>No comments yet</h3>
      )}
    </>
  );
};

export default PostPage;
