let instance = axios.create({
  baseURL: "https://simple-blog-api.crew.red/"
});

export const postsAPI = {
  getPosts() {
    return instance
      .get(`posts`)
      .then(response => response)
      .catch(error => {
        throw new Error(error);
      });
