const GET_POSTS = "posts/GET_POSTS";

const initialState = { posts: [] };

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_POSTS:
      return { counter: state.counter + 1 };
    case "DECREMENT":
      return { counter: state.counter - 1 };
    case "RESET":
      return { counter: 0 };
    default:
      return state;
  }
}
