const SET_POSTS = "posts/SET_POSTS";
const SET_CURRENT_COMMENTS = "posts/SET_CURRENT_COMMENTS";

import { postsAPI } from "../../api/api";

const setPosts = posts => ({
  type: SET_POSTS,
  payload: posts
});

const setCurrentComments = comments => ({
  type: SET_CURRENT_COMMENTS,
  payload: comments
});

export const getPosts = () => async dispatch => {
  const response = await postsAPI.getPosts();
  dispatch(setPosts(response));
};

export const getCurrentComments = id => async dispatch => {
  const response = await postsAPI.getComments(id);
  console.log(response);
};
